sap.ui.define([
	"sap/ui/core/mvc/Controller",
	"sap/m/MessageToast",
	"sap/ui/core/routing/History",
	"sap/ui/core/util/Export",
	"sap/ui/core/util/ExportTypeCSV",
	"sap/m/MessageBox",
	"sap/ui/model/Filter",
	"sap/ui/model/FilterOperator"

], function (Controller, MessageToast, History, Export, ExportTypeCSV, MessageBox, Filter, FilterOperator) {
	"use strict";
	return Controller.extend("moni.ZMF_MONITORING_DASHB.controller.Workflow_View", {
		/**
		 * Called when a controller is instantiated and its View controls (if available) are already created.
		 * Can be used to modify the View before it is displayed, to bind event handlers and do other one-time initialization.
		 * @memberOf moni.ZMF_MONITORING_DASHB.view.Workflow_View
		 */
		onInit: function () {

			var oRouter = sap.ui.core.UIComponent.getRouterFor(this);
			oRouter.getRoute("Workflow_View").attachMatched(this._onRouteFound, this);
		},
		// Bind UI element from backend data 
		_onRouteFound: function (oEvent) {

			this._setModel(); 
		},
		_setModel: function () {
          //Set the view with data
			this._oODataJSONModel = new sap.ui.model.json.JSONModel();
			this._oODataJSONModel.setData(sap.ui.getCore().getModel("WoWorkModel").getData());
			this.getView().setModel(this._oODataJSONModel, "WoWork");

		},
		onDataExport: function (oEvent) {

			var oExport = new Export({

				// Type that will be used to generate the content. Own ExportType's can be created to support other formats
				exportType: new ExportTypeCSV({
					separatorChar: ";"
				}),

				// Pass in the model created above
				models: this.getView().getModel("WoWork"),

				// binding information for the rows aggregation
				rows: {
					path: "/results"
				},

				// column definitions with column name and binding info for the content

				columns: [{
					name: "Date",
					template: {
						content: "{ path: 'WiCd', type: 'sap.ui.model.odata.type.Date', formatOptions: { style: 'medium' }}"
					}
				}, {
					name: "Time",
					template: {
						content: "{ path: 'WiCt', type: 'sap.ui.model.odata.type.Time', formatOptions: { style: 'medium' }}"
					}
				}, {

					name: "Work Item ID",
					template: {
						content: "{WiId}"
					}
				}, {
					name: "WI Text",
					template: {
						content: "{WiText}"
					}
				}, {
					name: "Client",
					template: {
						content: "{ZCLIENT}"
					}
				}, {
					name: "Task ID",
					template: {
						content: "{WiRhTask}"
					}
				}, {
					name: "WI Creator",
					template: {
						content: "{WiCreator}"
					}
				}]
			});

			// download exported file
			oExport.saveFile("Workflow Details").catch(function (oError) {
				MessageBox.error("Error when downloading data. Browser might not be supported!\n\n" + oError);
			}).then(function () {
				oExport.destroy();
			});
		},

		onSearch: function (oEvent) {
			// add filter for search
			var aFilters = [];
			var sQuery = oEvent.getSource().getValue();
			if (sQuery && sQuery.length > 0) {
				var ofilter = new Filter([
					new Filter("WiId", FilterOperator.Contains, sQuery),
					new Filter("WiText", FilterOperator.Contains, sQuery),
					new Filter("WiCreator", FilterOperator.Contains, sQuery),
					new Filter("WiRhTask", FilterOperator.Contains, sQuery),
					new Filter("ZCLIENT", FilterOperator.Contains, sQuery)
				], false);

				aFilters.push(ofilter);
			}

			// update table binding
			var oTable = this.byId("table0");
			var oBinding = oTable.getBinding("items");
			oBinding.filter(aFilters, "Application");
		},
		/**
		 *@memberOf moni.ZMF_MONITORING_DASHB.controller.Workflow_View
		 */
		goBack: function (oEvent) {
			//This code was generated by the layout editor.
			var oHistory, sPreviousHash;

			oHistory = History.getInstance();
			sPreviousHash = oHistory.getPreviousHash();

			if (sPreviousHash !== undefined) {
				window.history.go(-1);
			} else {
				this.getOwnerComponent().getRouter().navTo("RouteMaster_View", {}, true /*no history*/ );
			}
		}
	});
});